generated.css:
* { 
    padding: 0;
    margin: 0;
    outline: none;
}

body {
    background-color: #f2f4f7
}
  
#contianer {
    margin: auto;
}

header {
    background-color: #fff;
    box-shadow: 0px 2px 25px rgba(0, 0, 0, 0.2);
    padding: 5px 20px;
    display: flex;
    align-items: center;
    justify-content: space-between;
}

header img {
    margin-left: 20px;
}

.search-box {
    position: relative;
}

.search-icon {
    position: absolute;
    width: 20px;
    height: 20px;
    left: -10px;
    top: 5px;
}

input[type="text"] {
    background-color: #f2f4f7;
    height: 30px;
    border-radius: 20px;
    border-style: none;
    width: 250px;
    padding: 0 10px 0px 35px;
    font-size: 14px;
}

#content {
    display: flex;
    margin: 20px;
    background-color: #f2f4f7;
}

#content #recent_links {
    flex-grow: 2;
    margin-left: 20px;
    margin-right: 30px;
}
   
#content #most_viewed {
    flex-grow: 1;
    flex-basis: 600px;
}
  
#content #recent_links article {
    display: flex;
    margin-top: 10px;
}

.title {
    font-family: 'Source Sans Pro', sans-serif;
    font-weight: bold;
    font-size: 19px;
    margin-left: 10px;
    color: rgb(31, 31, 31);
}

article {
    margin: 0px 0px 10px 0px;
    background-color: #fff;
    padding: 10px;
}
  
article div:nth-child(2) {
    padding: 0px 0px 0px 10px;
    
}

h3 {
    font-family: 'Playfair Display', serif;
    text-transform: capitalize;
    font-size: 20px;
}
  
p {
    font-family: 'Source Sans Pro', sans-serif;
    margin-top: 10px;
}
  
article time {
    color:#399DF2;
    margin-top: 10px;
    font-family: 'Source Sans Pro', sans-serif;
    position: absolute;
    top: 100px;
    left: 10px;
}

article #text{
    margin-left: 14px;
    flex-grow: 1;
}

article #voter { 
    display: flex;
    flex-direction: column;
    justify-content: center;
    text-align: center;
    margin: 0 10px;
}

article #voter div {
    padding: 0;
}


.article-text {
    position: relative;
    height: 100%;
}

.news-container {
    position: relative;
    margin-top: 10px;
    width: 300px;
}

.news-text {
    background-color: rgba(0, 0, 0, 0.6);
    position: absolute;
    bottom: 5px;
    width: 100%;
    height: 300px;
    color: #f2f4f7;
}

.news-text h3 {
    margin: 20px 0 0 10px;
}

.news-text p {
    position: absolute;
    margin-left: 10px;
    top: 160px
}
